import React, { useEffect, useState } from 'react'
import Navbar from '../components/Navbar/Navbar'
import axios from 'axios'

const Home = ({isLoggedin, user}) => {

  const[blogs, setBlogs] = useState([])

  const fetchData=async()=>{
    try{
      const response = await axios.get('http://127.0.0.1:8000/home/') 
      console.log("API Response: ", response)
      setBlogs(response.data)
    }catch (error) {
        console.error('Error fetching data:', error.message);
    }
  }

  useEffect(()=>{
    fetchData()
  },[])

  return (
    <>
      <Navbar isLoggedin={isLoggedin} user={user}/>
      <div className="container mx-auto p-4">
        {blogs && blogs.length > 0 && (
          // blog of the day
          <div class="relative grid h-[40rem] w-full  flex-col items-end justify-center overflow-hidden rounded-xl bg-white bg-clip-border text-center text-gray-700">
          <div class="absolute inset-0 m-0 h-full w-full overflow-hidden rounded-none bg-transparent bg-[url('https://images.unsplash.com/photo-1552960562-daf630e9278b?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=687&q=80')] bg-cover bg-clip-border bg-center text-gray-700 shadow-none">
            <div class="absolute inset-0 w-full h-full to-bg-black-10 bg-gradient-to-t from-black/80 via-black/50"></div>
          </div>
          <div class="relative p-6 px-6 py-14 md:px-12">
            <h2 class="mb-6 block font-sans text-4xl font-medium leading-[1.5] tracking-normal text-white antialiased">
              How we design and code open-source projects?
            </h2>
            <h5 class="block mb-4 font-sans text-xl antialiased font-semibold leading-snug tracking-normal text-gray-400">
              Tania Andrew
            </h5>
            <img alt="Tania Andrew" src="https://images.unsplash.com/photo-1633332755192-727a05c4013d?ixlib=rb-1.2.1&amp;ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&amp;auto=format&amp;fit=crop&amp;w=1480&amp;q=80" class="relative inline-block h-[74px] w-[74px] !rounded-full border-2 border-white object-cover object-center" />
          </div>
        </div>  
        )}
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4">
          {blogs.slice(1).map((blog, key)=>{
            return(
              <div className='text-black flex flex-col' key={blog.id}>
                <h2>{blog.title}</h2>
                <h3>{blog.category}</h3>
                <p>{blog.content}</p>
              </div>
            )
          })}
        </div>
      </div>
    </>

  )
}

export default Home
