import './App.css';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Login from './pages/login';
import Register from './pages/register';
import Home from './pages/home';
import { useEffect, useState } from 'react';

function App() {
  const[IsLoggedin, setIsLoggedin] = useState(true)
  const[user, setUser] = useState({
    username:'',
  })

  console.log("Current user: ", user)

  useEffect(() => {
    const clearAccessToken = () => {
      localStorage.removeItem('access_token');
    };
    window.addEventListener('beforeunload', clearAccessToken);
    return () => {
      window.removeEventListener('beforeunload', clearAccessToken);
    };
  }, []);

  const handleLogin = () => {
    setIsLoggedin(true);
  };

  const handleLogout = () => {
    localStorage.removeItem('access_token');
    localStorage.removeItem('refresh_token');
    setIsLoggedin(false);
  };

  return (
    <Router>
      <div className="container mx-auto p-4">
        <Routes>
          <Route path="/login" element={<Login onLogin={handleLogin} onLogout={handleLogout} setUser={setUser}/>} />
          <Route path="/register" element={<Register onLogin={handleLogin} onLogout={handleLogout}/>} />
          <Route exact path='/' element={<Home isLoggedin = {IsLoggedin}  user = {user}/>}/>
        </Routes>
      </div>
    </Router>
  );
}
export default App;
