import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './pages/login';
import Register from './pages/register';
import Home from './pages/home';
import Test from './pages/test';
import BlogAdd from 'src/pages/blogAdd';
import PrivateRoutes from './utils/PrivateRoutes';

import {useState } from 'react';

// export const AppContext = createContext()


function App() {

  const[user, setUser] = useState({
    username:null,
    profile_image:null,
  })
  console.log(user)

  const handleLogout = () => {
    localStorage.removeItem('access_token');
    localStorage.removeItem('refresh_token');
  };


  return (
    // <AppContext.Provider value={{user, setUser}}>
      <Router>
        <div className="container mx-auto p-4">
          <Routes>
            {/* <Route element={<PrivateRoutes />}>
                <Route element={<Home/>} path="/" exact/>
                <Route element={<BlogAdd/>} path="/addBlog"/>
            </Route> */}

            <Route path="/login" element={<Login setUser={setUser}/>} />
            <Route path="/register" element={<Register onLogout={handleLogout}/>} />
            <Route path='/test' element={<Test/>}></Route>

            <Route path='/' element={<Home handleLogout={handleLogout} setUser={setUser} user={user}/>}/>
            <Route path='/addBlog' element={ localStorage.getItem('access_token') ? <BlogAdd user={user}/> : <Navigate to="/login" replace/>}></Route>

          </Routes>
        </div>
      </Router>
    // </AppContext.Provider>
  );
}
export default App;
