import './App.css';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './pages/login';
import Register from './pages/register';
import Home from './pages/home';
import Test from './pages/test';
import BlogAdd from 'src/pages/blogAdd';

import { useEffect, useState } from 'react';

function App() {

  // const[isLoggedin, setIsLoggedin] = useState(false)

  // const[user, setUser] = useState({
  //   username:'',
  // })

  // console.log("USERNAME:", user)
  // console.log("LOGIN:", isLoggedin)
  // console.log("Current user: ", user)

  useEffect(() => {
    const storedIsLoggedin = localStorage.getItem('IsLoggedin') === 'true'
    const storedUser = JSON.parse(localStorage.getItem('user'));

    if (storedIsLoggedin) {setIsLoggedin(true);}
    if (storedUser) {setUser(storedUser);}
  }, []);

  const handleLogin = () => {
    setIsLoggedin(true);
    localStorage.setItem('IsLoggedin','true')
  };

  const handleLogout = () => {
    localStorage.removeItem('access_token');
    localStorage.removeItem('refresh_token');
    localStorage.removeItem('IsLoggedin');
    localStorage.removeItem('user')
    setIsLoggedin(false);
  };

  return (
    <Router>
      <div className="container mx-auto p-4">
        <Routes>

          <Route path='/' element={<Home isLoggedin = {isLoggedin}  user = {user} handleLogout={handleLogout}/>}/>
          <Route path="/login" element={<Login onLogin={handleLogin} onLogout={handleLogout} setUser={setUser}/>} />

          <Route path="/register" element={<Register onLogin={handleLogin} onLogout={handleLogout}/>} />
          <Route path='/test' element={<Test/>}></Route>
          <Route path='/addBlog' element={ isLoggedin ? <BlogAdd user={user}/> : <Navigate to="/login" replace/>}></Route>


        </Routes>
      </div>
    </Router>
  );
}
export default App;
