const Home = () => {

  const[blogs, setBlogs] = useState([])

  const fetchData=async()=>{
    try{
      const response = await axios.get('http://127.0.0.1:8000/') 
      setBlogs(response.data) 
    }catch (error) {
        console.error('Error fetching data:', error.message);
    }
  }
  useEffect(()=>{
    fetchData()
    console.log(blogs)
  },[])

  return (
    <>
