import './App.css';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './pages/login';
import Register from './pages/register';
import Home from './pages/home';
import Test from './pages/test';
import BlogAdd from 'src/pages/blogAdd';

import { useEffect, useState } from 'react';

function App() {
  const[IsLoggedin, setIsLoggedin] = useState(false)

  const[user, setUser] = useState({
    username:'',
  })
  console.log("USERNAME:", user)
  console.log("LOGIN:", IsLoggedin)

  console.log("Current user: ", user)

  useEffect(() => {
    const clearAccessToken = () => {
      localStorage.removeItem('access_token');
    };
    window.addEventListener('beforeunload', clearAccessToken);
    return () => {
      window.removeEventListener('beforeunload', clearAccessToken);
    };
  }, []);

  const handleLogin = () => {
    setIsLoggedin(true);
  };

  const handleLogout = () => {
    localStorage.removeItem('access_token');
    localStorage.removeItem('refresh_token');
    setIsLoggedin(false);
  };

  return (
    <Router>
      <div className="container mx-auto p-4">
        <Routes>
          <Route path="/login" element={<Login onLogin={handleLogin} onLogout={handleLogout} setUser={setUser}/>} />
          <Route path="/register" element={<Register onLogin={handleLogin} onLogout={handleLogout}/>} />
          <Route path='/test' element={<Test/>}></Route>
          <Route path='/addBlog' element={ IsLoggedin ? <BlogAdd/> : <Navigate to="/login" re />}></Route>
          <Route path='/' element={<Home isLoggedin = {IsLoggedin}  user = {user}/>}/>
        </Routes>
      </div>
    </Router>
  );
}
export default App;
