import './App.css';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './pages/login';
import Register from './pages/register';
import Home from './pages/home';
import Test from './pages/test';
import BlogAdd from 'src/pages/blogAdd';

import { useEffect, useState, createContext } from 'react';

export const AppContext = createContext()


function App() {


  const handleLogout = () => {
    localStorage.removeItem('access_token');
    localStorage.removeItem('refresh_token');
    localStorage.removeItem('IsLoggedin');
    localStorage.removeItem('user')
  };

  const [login, setLogin] = useState(false)
  const[user, setUser] = useState('')

  useEffect(()=>{
    console.log(login)
    if(localStorage.getItem('user')){
      setLogin(true)
    }
  },[login])

  return (
    <Router>
      <div className="container mx-auto p-4">
        <Routes>

          <Route path='/' element={<Home handleLogout={handleLogout} user={user}/>}/>
          <Route path="/login" element={<Login setUser={setUser}/>} />

          <Route path="/register" element={<Register onLogout={handleLogout}/>} />
          <Route path='/test' element={<Test/>}></Route>

          <Route path='/addBlog' element={ login ? <BlogAdd/> : <Navigate to="/login" replace/>}></Route>

        </Routes>
      </div>
    </Router>
  );
}
export default App;
